mapper.select:
select *
from
(
    select rownum as RNUM, A.*
    from(

	        select * from products


        	select * from PRODUCTEVENTVIEW
			where eventname=#{eventName}

        order by productNo desc
    )A    
)

where RNUM>#{firstRecordIndex}
  and RNUM<=#{firstRecordIndex }+ #{recordCountPerPage}
